INSERT INTO `app_test_evaluate` (`test_id`, `lang_id`, `gend_id`, `eval_from`, `eval_to`, `eval_text`) VALUES (2, 1, 1, 1.00,50.00, "دماغك أكثر ذكورة من المعتاد ومستوى التستوسترون في الجسم أعلى.أنت من الأشخاص يتّسمون عادة بالعقلانية والحزم والقدرة على التحليل وصياغة الكلام، ويميلون إلى السلوك الانضباطي والمنظم جيداً والمُحافظ. وقدرتك على التدبير المالي وعلى القيام بالإحصائيات عالية، بينما تلعب المشاعر عندهم دوراً ضئيلاً أو ليس لها دور على الإطلاق. وتميلون للعنف الجسدي الأقرب إلى السادية.");
INSERT INTO `app_test_evaluate` (`test_id`, `lang_id`, `gend_id`, `eval_from`, `eval_to`, `eval_text`) VALUES (2, 1, 1, 51.00,150.00, "أنتم من ذوي السمات الذكرية المنطقية الكلاسيكية والنمطية والمحافظة في التفكير إلى حد ما. يمكنك التكيّف نسيباً مع إمرأة مطواعة ونمطية ولا تمارس التفكير كثيراً أو النقد وهن نساء قليلات في الحياة.");
INSERT INTO `app_test_evaluate` (`test_id`, `lang_id`, `gend_id`, `eval_from`, `eval_to`, `eval_text`) VALUES (2, 1, 1, 151.00,180.00, "أنتم من ذوي السمات الذكرية المنطقية الكلاسيكية والنمطية والمحافظة في التفكير إلى حد ما. يمكنك التكيّف نسيباً مع إمرأة مطواعة ونمطية ولا تمارس التفكير كثيراً أو النقد وهن نساء قليلات في الحياة.");
INSERT INTO `app_test_evaluate` (`test_id`, `lang_id`, `gend_id`, `eval_from`, `eval_to`, `eval_text`) VALUES (2, 1, 1, 181.00,200.00, "أنتم ذوو صفات (الأنثوية) العالية للدماغ وهنا إمكانية المواهب الإبداعية، الفنية و الموسيقية، أكبر لديكم. و يتخذ هؤلاء الأشخاص قراراتهم بشكل عفوي معتمدين على حدسهم. وهم يستشعرون بعض المشاكل التي لا يتنبّه إليها الآخرون، ويستطيعون حلّها بشكل خلاق وبمقدرة كبيرة على تفّهم الآخرين.");
INSERT INTO `app_test_evaluate` (`test_id`, `lang_id`, `gend_id`, `eval_from`, `eval_to`, `eval_text`) VALUES (2, 1, 1, 201.00,999.00, "إمكانية الميل المثلي جنسياً أكبر لديكم، وأنت من النوع ذي التفكير الأنثوي وهنالك حالة ندعوها عدم الانسجام بين هويتك الجنسية وهويتك العقلية ومن الصعب أن تقيم علاقة عاطفية أو جنسية مع النساء إلا كما لو أنها علاقة سُحاقية.");
