INSERT INTO `app_test_evaluate` (`test_id`, `lang_id`, `gend_id`, `eval_from`, `eval_to`, `eval_text`) VALUES (2, 1, 2, 151.00,180.00, "أنتم من النوع ذي الازدواجية الجنسية في التفكير وأنتم أفضل الناس في الذكاء الانفعالي (الوجداني).أنتم لا تُظهرون ما يسمى بطريقة تفكير ذكرية صرفة أو أنثوية صرفة وإنما تتمتعون بتفكير ليّن له أهمية بالغة ضمن المجموعة التي تبحث عن حل للمشاكل. ويكون لدى هؤلاء الأشخاص موهبة طبيعية لعقد صداقات مع الجنسين. ولا مشكلة لديكم بالتكيف مع الآخرين ولكن تحتاجون من الجنس الآخر من يُقدّر مواهبهم العالية");
